import numpy as np
L1 = 5
L2 = 7
L3 = 11
FITNESS_MATCH = 2

##########
# LFSRs
##########

def geffe(l:int, k1:list, k2:list, k3:list):
    x1 = np.array(LFSR_calc(LFSR1, l, k1))
    x2 = np.array(LFSR_calc(LFSR2, l, k2))
    x3 = np.array(LFSR_calc(LFSR3, l, k3))
    return ((x1*x2+x2*x3+x3)%2).tolist()

def LFSR_calc(lfsr, l:int, k):
    z = k.copy()
    for i in range(l - len(k)):
        x = lfsr(k)
        k = k[1:] + [x]
        z.append(x)
    return z


def LFSR1(k: list):
    # x^5 + x^2 + 1
    return (k[-5] + k[-2]) % 2

def LFSR2(k: list):
    # x^7 + x^1 + 1
    return (k[-7] + k[-1]) % 2

def LFSR3(k: list):
    # x^11 + x^2 + 1
    return (k[-11] + k[-2]) % 2 

##########
# Data representation conversion
##########
def toBits(x:str):
    return [i for c in str.encode(x) for i in intToBits(c - 65) ]

def intToBits(x:int):
    z = [int(b) for b in bin(x)[2:]]
    return z[-5:] if len(z)>=5 else [0]*(5-len(z))+z 

# x is list of bits
def toString(x:list):
    arr = []
    for i in range(0,len(x), 5):
        arr.append(bitsToInt(x[i:i+5])+65)
    return bytes(arr).decode()

def bitsToInt(x:list):
    return 16*x[0]+8*x[1]+4*x[2] + 2*x[3] + x[4]

def listToStr(x:list):
    s = ""
    for i in x:
        s+=str(i)
    return s

##########
# Helpers
##########

def inc_key(k:list, depth:int):
    if k[depth] == 0:
        k[depth] = 1
        return k
    k[depth] = 0
    return inc_key(k, depth-1)

def count_matching(l1:list, l2:list):
    return sum([1 if l1[i]==l2[i] else 0 for i in range(len(l1))])

def get_key1_3(diff, lfsr_f, k):
    known_len = len(diff)
    expected_match = 3/4 * known_len
    k_best = []

    for _ in range(pow(2, len(k))):
        out = LFSR_calc(lfsr_f, known_len, k)

        fitness =abs(count_matching(out, diff) - expected_match)
        if(fitness <=  FITNESS_MATCH):
            k_best.append(k.copy())

        k = inc_key(k, len(k)-1)
    return k_best

def get_key2(diff, k, k1,k3):
    for _ in range(pow(2, len(k))):
        out = geffe(len(diff), k1, k, k3)
        if out == diff:
            return k

        k = inc_key(k, len(k)-1)
    return None


